def relative_to_assets(path: str) -> Path:
    return ASSETS_PATH / Path(path)

def sphereRadius(r):
    sphereDiameter = 2 * r
    circumference = pi * sphereDiameter

    sphereArea = 4 * pi * (r ** 2)
    sphereVolume = (4/3) * pi * (r ** 3)

    return sphereArea, sphereVolume, sphereDiameter, circumference

def calculateSphere():
    try:
        # Get the input radius from entry_1 widget
        radius = float(entry_1.get())

        # Calculate sphere properties
        area, volume, diameter, circumference = sphereRadius(radius)

        # Format the results to display two decimal points
        area = "{:.2f}".format(area)
        volume = "{:.2f}".format(volume)
        diameter = "{:.2f}".format(diameter)
        circumference = "{:.2f}".format(circumference)

        # Display the results in the text widget
        result_text = f"Surface Area: {area}\nVolume: {volume}\nDiameter: {diameter}\nCircumference: {circumference}"
        result_display.config(state="normal")
        result_display.delete(1.0, "end")
        result_display.insert("end", result_text)
        result_display.config(state="disabled")

    except ValueError:
        # Handle invalid input (non-numeric)
        result_display.config(state="normal")
        result_display.delete(1.0, "end")
        result_display.insert("end", "Invalid input: Please enter a number.")
        result_display.config(state="disabled")

